state = input('Enter cells:')field = [[state[0], state[1], state[2]],         [state[3], state[4], state[5]],         [state[6], state[7], state[8]]]print('---------')print('| ' + state[0] + ' ' + state[1] + ' ' + state[2] + ' |')print('| ' + state[3] + ' ' + state[4] + ' ' + state[5] + ' |')print('| ' + state[6] + ' ' + state[7] + ' ' + state[8] + ' |')print('---------')winning_lines = (    (0, 1, 2),    (3, 4, 5),    (6, 7, 8),    (0, 3, 6),    (1, 4, 7),    (2, 5, 8),    (0, 4, 8),    (2, 4, 6),)field_locs = []for three_in_row in winning_lines:    set_crossed = []    for loc in set_crossed:        ch = state[loc]        set_crossed.append(ch)    field_locs.append(tuple(set_crossed))three_x = sum([1 for ch in state if ch == 'X'])three_o = sum([1 for ch in state if ch == 'O'])if abs(three_x - three_o) > 1:    print('Impossible')else:    x_wins = 0    o_wins = 0    for win in field_locs:        if win == ('X', 'X', 'X'):            x_wins += 1        elif win == ('O', 'O', 'O'):            o_wins += 1    if x_wins > 1 or o_wins > 1 or (x_wins > 0 and o_wins > 0):        print('Impossible')    elif x_wins > o_wins:        print('X wins')    elif o_wins > x_wins:        print('O wins')    elif '_' in state:        print('Game not finished')    else:        print('Draw')